// export default App;

import React, { useState } from 'react';
import './App.css';
import TaskCard from './TaskCard';  // For displaying individual tasks

const App = () => {
  // State to hold tasks
  const [tasks, setTasks] = useState([]);
  const [taskInput, setTaskInput] = useState('');  // State to manage the input field

  // Add a task
  const addTask = () => {
    if (taskInput.trim() === '') return;  // Don't add empty tasks
    const newTask = {
      id: Date.now(),
      title: taskInput,
      stage: 'To Do',  // Default stage
    };
    setTasks([...tasks, newTask]);
    setTaskInput('');  // Clear input field
  };

  // Move task to another stage
  const moveTask = (taskId, newStage) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, stage: newStage } : task
    );
    setTasks(updatedTasks);
  };

  // Delete a task
  const deleteTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  // Group tasks by stage
  const stages = ['To Do', 'In Progress', 'Done'];

  return (
    <div className="App">
      <h1>Task Tracker</h1>
      <input
        type="text"
        value={taskInput}
        onChange={(e) => setTaskInput(e.target.value)}  // Update input field
        placeholder="New task..."
      />
      <button onClick={addTask}>Add Task</button>

      <div className="board">
        {stages.map(stage => (
          <div key={stage} className="stage">
            <h2>{stage}</h2>
            {tasks.filter(task => task.stage === stage).map(task => (
              <TaskCard
                key={task.id}
                task={task}
                moveTask={moveTask}
                deleteTask={deleteTask}
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
