import React, { useState } from 'react';

const TaskCard = ({ task, moveTask, deleteTask, editTask, assignPerson }) => {
  const [isEditing, setIsEditing] = useState(false);  // Track if the task is being edited
  const [newTitle, setNewTitle] = useState(task.title);  // Input value for the new task title

  // Handle editing task title
  const handleEditTitle = () => {
    if (newTitle.trim()) {
      editTask(task.id, newTitle);  // Save the new title
      setIsEditing(false);  // Exit edit mode
    }
  };

  return (
    <div className="task-card">
      <div>
        {/* If editing, show input; otherwise, show task title */}
        {/* {isEditing? (
          <div>
            <input
              type="text"
              value={newTitle}
              onChange={(e) => setNewTitle(e.target.value)}  // Update title while editing
            />
            <button onClick={handleEditTitle}>Save</button>
          </div>
        ) 
        : (
          <h3 onClick={() => setIsEditing(true)}>{task.title}</h3>  // Click to edit
        
        )
} */}
<h3 onClick={() => setIsEditing(true)}>{task.title}</h3>
        
      </div>

      <div>
        <strong>Assigned to: </strong>
        <span>{task.assignedTo}</span>
        {/* Dropdown to assign person
        <select 
          onChange={(e) => assignPerson(task.id, e.target.value)} 
          value={task.assignedTo}
        >
          <option value="Unassigned">Unassigned</option>
          <option value="Alice">Alice</option>
          <option value="Bob">Bob</option>
          <option value="Charlie">Charlie</option>
        </select> */}
      </div>

      <div className="task-actions">
        {/* Move task buttons */}
        {task.stage !== 'To Do' && <button onClick={() => moveTask(task.id, 'To Do')}>To Do</button>}
        {task.stage !== 'In Progress' && <button onClick={() => moveTask(task.id, 'In Progress')}>In Progress</button>}
        {task.stage !== 'Done' && <button onClick={() => moveTask(task.id, 'Done')}>Done</button>}

        {/* Delete task */}
        <button onClick={() => deleteTask(task.id)}>Delete</button>
        <button onClick={() => setIsEditing(true)}>Edit</button>
      </div>
    </div>
  );
};

export default TaskCard;
